mapper.select:
SELECT id, group_code, group_name, vocab_count, answer_count, wrong_answer_count, complete_flag, DATE_FORMAT(regist_datetime, '%Y-%m-%d %H:%i:%s') as regist_datetime, DATE_FORMAT(update_datetime, '%Y-%m-%d %H:%i:%s') as update_datetime, username, study_time_seconds, round, question_type_code FROM vocab_question_result WHERE username = #{username}
and group_code = #{groupCode}
and id = #{id}
ORDER BY update_datetime asc

mapper.update:
UPDATE vocab_question_result
SET 
	answer_count=#{answer_count},
	wrong_answer_count=#{wrong_answer_count},
	complete_flag=#{complete_flag},
	update_datetime=now(),
	study_time_seconds=#{study_time_seconds}
WHERE id=#{id}
    and username =#{username}

mapper.delete:
DELETE FROM voca_study.vocab_question_result
WHERE 
	username = #{username}

		and group_code = #{group_code}


		and id = #{id}
