mapper.select:
SELECT id, vocab_question_result_id, group_code, question_type, question_value, vocab_id, vocab, mean, answer_vocab_id, answer_vocab, answer_mean, result_flag, regist_datetime, username FROM vocab_question_result_history WHERE username = #{username}
and group_code = #{group_code}
and id = #{id}

mapper.delete:
DELETE FROM vocab_question_result_history
WHERE
	username = #{username}

		and group_code = #{group_code}


		and id = #{id}
